import React, { useState, useEffect, useRef } from "react";
import VideoChat from "./components/VideoChat";
import TextChat from "./components/TextChat";
import { io } from "socket.io-client";
import "./App.css";

// Create a single socket instance for the whole app
const socket = io("https://chatify-lyil.onrender.com");

function App() {
  const [connected, setConnected] = useState(false);
  const [status, setStatus] = useState("Click 'Start Chat' to begin.");
  const [isVideoEnabled, setIsVideoEnabled] = useState(true);
  const [isAudioEnabled, setIsAudioEnabled] = useState(true);
  const videoChatRef = useRef(null);

  // Listen for partner events
  useEffect(() => {
    socket.on("waiting", () => {
      setStatus("Waiting for a partner...");
    });

    socket.on("paired", () => {
      setStatus("Connected to a stranger!");
    });

    socket.on("partner-disconnected", () => {
      setStatus("Partner disconnected. Reconnecting...");
    });

    // Cleanup on unmount
    return () => {
      socket.off("waiting");
      socket.off("paired");
      socket.off("partner-disconnected");
    };
  }, []);

  // Start chat
  const startChat = () => {
    setConnected(true);
    socket.emit("join"); // Optional: signal backend that user wants to join queue
  };

  // Skip partner
  const handleSkip = () => {
    setStatus("Skipping current partner...");
    socket.emit("skip");
  };

  // Toggle video
  const toggleVideo = () => {
    if (videoChatRef.current) {
      videoChatRef.current.toggleVideo();
    }
  };

  // Toggle audio
  const toggleAudio = () => {
    if (videoChatRef.current) {
      videoChatRef.current.toggleAudio();
    }
  };

  return (
    <div className="container">
      <nav className="navbar">
        <h1 className="navbar-title">Chatify</h1>
        <p className="status">{status}</p>
      </nav>

      {!connected && (
        <div className="start-button-container">
          <button className="start-btn" onClick={startChat}>
            Start Chat
          </button>
        </div>
      )}

      {connected && (
        <>
          <div className="chat-layout">
            <div className="video-section">
              <VideoChat socket={socket} />
            </div>
            <div className="chat-section">
              <TextChat socket={socket} />
            </div>
          </div>
          <div className="skip-button-container">
            <button className="skip-btn" onClick={handleSkip}>
              Skip & Connect Next
            </button>
          </div>
        </>
      )}
    </div>
  );
}

export default App;
