import React, { useState, useEffect } from "react";
import VideoChat from "./components/VideoChat";
import TextChat from "./components/TextChat";
import { io } from "socket.io-client";
import "./App.css";

// Create a single socket instance for the whole app
const socket = io("https://chatify-lyil.onrender.com");

function App() {
  const [connected, setConnected] = useState(false);
  const [status, setStatus] = useState("Click 'Start Chat' to begin.");

  // Listen for partner events
  useEffect(() => {
    socket.on("waiting", () => {
      setStatus("Waiting for a partner...");
    });

    socket.on("paired", () => {
      setStatus("Connected to a stranger!");
    });

    socket.on("partner-disconnected", () => {
      setStatus("Partner disconnected. Reconnecting...");
    });

    // Cleanup on unmount
    return () => {
      socket.off("waiting");
      socket.off("paired");
      socket.off("partner-disconnected");
    };
  }, []);

  // Start chat
  const startChat = () => {
    setConnected(true);
    socket.emit("join"); // Optional: signal backend that user wants to join queue
  };

  // Skip partner
  const handleSkip = () => {
    setStatus("Skipping current partner...");
    socket.emit("skip");
  };

  return (
    <div className="container">
      <h1> Chatify </h1>
      <p className="status">{status}</p>

      {!connected && (
        <button className="start-btn" onClick={startChat}>
          Start Chat
        </button>
      )}

      {connected && (
        <>
          <VideoChat socket={socket} />
          <TextChat socket={socket} />
          <button className="skip-btn" onClick={handleSkip}>
            Skip & Connect Next
          </button>
        </>
      )}
    </div>
  );
}

export default App;
